import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import HeroSlide from './HeroSlide/HeroSlide';
import hero1 from '../../assets/images/hero1.jpg';
import hero6 from '../../assets/images/hero6.jpeg';
import hero4 from '../../assets/images/hero4.jpg';

import './Hero.css';

const Hero = () => {
  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    fade: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    className: 'slides',
  };
  return (
    <Slider {...settings}>
      <HeroSlide
        image={hero1}
        titleOne="New Inspiration 2021"
        titleTwo="UP TO 70% OFF ON SALES"
        titleThree="Trending from men and women style collection"
        btnOne="shop women's"
        btnTwo="shop men's"
      />
      <HeroSlide
        image={hero6}
        titleOne="New Inspiration 2021"
        titleTwo="CLOTHING MADE FOR YOU!"
        titleThree="Trending from men and women style collection"
        btnOne="shop women's"
        btnTwo="shop men's"
      />
      <HeroSlide
        image={hero4}
        titleOne="New Inspiration 2021"
        titleTwo="GET ALL EXCLUSIVE OFFERS FOR THIS SEASON"
        titleThree="Trending from men and women style collection"
        btnOne="shop women's"
        btnTwo="shop men's"
        differentClassName
      />
    </Slider>
  );
};

export default Hero;
