import React, {useState} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Login.css';

const Register = () => {
  const [emailReg, setEmailReg] = useState('');
  const [passwordReg, setPasswordReg] = useState('');
  console.log(emailReg, passwordReg);
  const register = () => {
    console.log('clicked');
    axios.get('http://localhost:5000')
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }
  return (
    <div className="login">
      <div className="login__container">
        <form>
          <h5>E-mail</h5>
          <input type="text" onChange={(e) => setEmailReg(e.target.value)}/>

          <h5>Password</h5>
          <input type="password" onChange={(e) => setPasswordReg(e.target.value)} />

          <button type="submit" className="login__loginButton" onClick={register}>
            Register
          </button>
        </form>

        <Link to='/login' className="login__registerButton">Already Sign In? Log In here </Link>
      </div>
    </div>
  );
};

export default Register;
